import { useState } from "react";
import { Search, ArrowUpDown, ChevronLeft, ChevronRight } from "lucide-react";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import type { PayoutRow } from "@shared/schema";

interface PayoutDataTableProps {
  rows: PayoutRow[];
  adjustments: Array<{
    orderId: string;
    reason: string;
    amount: number;
    reference: string;
  }>;
  isLoading?: boolean;
}

export default function PayoutDataTable({ rows, adjustments, isLoading }: PayoutDataTableProps) {
  const [searchQuery, setSearchQuery] = useState("");
  const [sortField, setSortField] = useState<keyof PayoutRow>("orderId");
  const [sortDirection, setSortDirection] = useState<"asc" | "desc">("asc");
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10);

  const formatCurrency = (amount: number) => {
    // Display zero amounts as â‚¹0 without hiding them
    if (amount === 0) return 'â‚¹0';
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const getStatusBadge = (status: string) => {
    const statusLower = status.toLowerCase();
    if (statusLower.includes('delivered')) {
      return <Badge variant="default" className="bg-green-100 text-green-800">Delivered</Badge>;
    }
    if (statusLower.includes('rts') || statusLower.includes('rto')) {
      return <Badge variant="destructive" className="bg-red-100 text-red-800">RTS</Badge>;
    }
    return <Badge variant="secondary">{status}</Badge>;
  };

  // Handle undefined data with safe defaults
  const safeRows = rows || [];
  const safeAdjustments = adjustments || [];

  // Filter rows based on search query
  const filteredRows = safeRows.filter((row) => {
    const query = searchQuery.toLowerCase();
    return (
      row.orderId.toLowerCase().includes(query) ||
      row.waybill?.toLowerCase().includes(query) ||
      row.product.toLowerCase().includes(query) ||
      row.skuUid.toLowerCase().includes(query)
    );
  });

  // Sort rows with safe filtering
  const sortedRows = [...filteredRows].sort((a, b) => {
    const aValue = a[sortField];
    const bValue = b[sortField];

    if (typeof aValue === 'string' && typeof bValue === 'string') {
      return sortDirection === 'asc' 
        ? aValue.localeCompare(bValue)
        : bValue.localeCompare(aValue);
    }

    if (typeof aValue === 'number' && typeof bValue === 'number') {
      return sortDirection === 'asc' ? aValue - bValue : bValue - aValue;
    }

    return 0;
  });

  // Pagination
  const totalPages = Math.ceil(sortedRows.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const paginatedRows = sortedRows.slice(startIndex, startIndex + itemsPerPage);

  const handleSort = (field: keyof PayoutRow) => {
    if (field === sortField) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setSortField(field);
      setSortDirection('asc');
    }
    setCurrentPage(1);
  };

  if (isLoading) {
    return (
      <div className="space-y-4">
        <div className="flex items-center justify-between">
          <div className="relative">
            <Input
              placeholder="Search orders..."
              className="w-64 pr-10"
              disabled
            />
            <Search className="absolute right-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
          </div>
        </div>
        <div className="rounded-md border">
          <div className="p-8 text-center">
            <div className="animate-spin mx-auto mb-4 w-8 h-8 border-4 border-primary border-t-transparent rounded-full"></div>
            <p className="text-sm text-gray-500">Loading payout data...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-3 md:space-y-4 mx-2 md:mx-0">
      {/* Search and Controls */}
      <div className="flex flex-col sm:flex-row items-start sm:items-center justify-between gap-3 sm:gap-0">
        <div className="relative w-full sm:w-64">
          <Input
            placeholder="Search orders..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="w-full pr-10 text-sm"
            data-testid="input-search-orders"
          />
          <Search className="absolute right-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
        </div>
        <div className="text-xs sm:text-sm text-gray-500">
          Showing {Math.min(startIndex + 1, sortedRows.length)} to {Math.min(startIndex + itemsPerPage, sortedRows.length)} of {sortedRows.length} results
        </div>
      </div>

      {/* Adjustments Summary */}
      {safeAdjustments.length > 0 && (
        <div className="bg-orange-50 border border-orange-200 rounded-md p-4">
          <h4 className="text-sm font-medium text-orange-800 mb-2">RTS/RTO Adjustments</h4>
          <p className="text-sm text-orange-700">
            {safeAdjustments.length} order(s) have been adjusted due to status changes from Delivered to RTS/RTO.
          </p>
        </div>
      )}

      {/* Table */}
      <div className="rounded-md border overflow-x-auto">
        <Table className="min-w-[800px]">
          <TableHeader>
            <TableRow>
              <TableHead>
                <Button
                  variant="ghost"
                  size="sm"
                  className="h-auto p-0 font-medium hover:bg-transparent"
                  onClick={() => handleSort('orderId')}
                  data-testid="sort-order-id"
                >
                  <div className="flex items-center space-x-1">
                    <span>Order ID</span>
                    <ArrowUpDown className="h-3 w-3" />
                  </div>
                </Button>
              </TableHead>
              <TableHead>
                <Button
                  variant="ghost"
                  size="sm"
                  className="h-auto p-0 font-medium hover:bg-transparent"
                  onClick={() => handleSort('waybill')}
                  data-testid="sort-waybill"
                >
                  <div className="flex items-center space-x-1">
                    <span>Waybill</span>
                    <ArrowUpDown className="h-3 w-3" />
                  </div>
                </Button>
              </TableHead>
              <TableHead>Product</TableHead>
              <TableHead>SKU/UID</TableHead>
              <TableHead className="text-center">Shipped Qty</TableHead>
              <TableHead className="text-center">Delivered Qty</TableHead>
              <TableHead className="text-right">COD Rate</TableHead>
              <TableHead className="text-right">COD Received</TableHead>
              <TableHead className="text-right">Shipping Cost</TableHead>
              <TableHead className="text-right">Product Cost</TableHead>
              <TableHead className="text-right">Payable</TableHead>
              <TableHead>Status</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {paginatedRows.map((row, index) => (
              <TableRow key={`${row.orderId}-${index}`} data-testid={`row-order-${row.orderId}`}>
                <TableCell className="font-medium">{row.orderId}</TableCell>
                <TableCell className="text-gray-500">{row.waybill || "-"}</TableCell>
                <TableCell>{row.product}</TableCell>
                <TableCell className="text-gray-500">{row.skuUid}</TableCell>
                <TableCell className="text-center">{row.shippedQty}</TableCell>
                <TableCell className="text-center">{row.deliveredQty}</TableCell>
                <TableCell className="text-right">{formatCurrency(row.codRate)}</TableCell>
                <TableCell className="text-right font-medium text-green-600">
                  {row.codReceived > 0 ? formatCurrency(row.codReceived) : "-"}
                </TableCell>
                <TableCell className="text-right">{formatCurrency(row.shippingCost)}</TableCell>
                <TableCell className="text-right">{formatCurrency(row.productCost)}</TableCell>
                <TableCell className={`text-right font-semibold ${
                  row.payable > 0 ? 'text-primary' : 'text-red-600'
                }`}>
                  {formatCurrency(row.payable)}
                </TableCell>
                <TableCell>
                  {getStatusBadge(row.status)}
                </TableCell>
              </TableRow>
            ))}
            {paginatedRows.length === 0 && (
              <TableRow>
                <TableCell colSpan={12} className="text-center py-8 text-gray-500">
                  {searchQuery ? 'No orders match your search criteria.' : 'No payout data available.'}
                </TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </div>

      {/* Pagination */}
      {totalPages > 1 && (
        <div className="flex flex-col sm:flex-row items-start sm:items-center justify-between gap-3 sm:gap-0">
          <div className="text-xs sm:text-sm text-gray-500">
            Page {currentPage} of {totalPages}
          </div>
          <div className="flex items-center space-x-1 sm:space-x-2">
            <Button
              variant="outline"
              size="sm"
              onClick={() => setCurrentPage(currentPage - 1)}
              disabled={currentPage === 1}
              data-testid="button-previous-page"
              className="text-xs sm:text-sm px-2 sm:px-3"
            >
              <ChevronLeft className="h-3 w-3 sm:h-4 sm:w-4" />
              <span className="hidden sm:inline ml-1">Previous</span>
            </Button>
            <div className="hidden md:flex items-center space-x-1">
              {[...Array(Math.min(5, totalPages))].map((_, i) => {
                const pageNum = i + 1;
                return (
                  <Button
                    key={pageNum}
                    variant={currentPage === pageNum ? "default" : "outline"}
                    size="sm"
                    onClick={() => setCurrentPage(pageNum)}
                    data-testid={`button-page-${pageNum}`}
                    className="text-xs sm:text-sm px-2 sm:px-3"
                  >
                    {pageNum}
                  </Button>
                );
              })}
            </div>
            <Button
              variant="outline"
              size="sm"
              onClick={() => setCurrentPage(currentPage + 1)}
              disabled={currentPage === totalPages}
              data-testid="button-next-page"
              className="text-xs sm:text-sm px-2 sm:px-3"
            >
              <span className="hidden sm:inline mr-1">Next</span>
              <ChevronRight className="h-3 w-3 sm:h-4 sm:w-4" />
            </Button>
          </div>
        </div>
      )}
    </div>
  );
}

client/src/components/quick-setup.tsx.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { Settings, Download, FileSpreadsheet } from "lucide-react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";

export default function QuickSetup() {
  const [isSettingUp, setIsSettingUp] = useState(false);
  const { toast } = useToast();

  const setupDefaultsMutation = useMutation({
    mutationFn: () => apiRequest('POST', '/api/setup-defaults'),
    onSuccess: () => {
      toast({
        title: "Setup completed",
        description: "Default shipping rates and sample product prices have been added.",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/shipping-rates'] });
      queryClient.invalidateQueries({ queryKey: ['/api/product-prices'] });
    },
    onError: () => {
      toast({
        title: "Setup failed",
        description: "Could not create default settings. Please try again.",
        variant: "destructive",
      });
    }
  });

  const handleSetupDefaults = async () => {
    setIsSettingUp(true);
    await setupDefaultsMutation.mutateAsync();
    setIsSettingUp(false);
  };

  const downloadSampleExcel = () => {
    // Create sample Excel data
    const sampleData = [
      ['Dropshipper Email', 'Order ID', 'Order Date', 'Waybill', 'Product Name', 'SKU', 'Qty', 'COD Amount', 'Status', 'Delivered Date', 'RTS Date', 'Shipping Provider'],
      ['dropshipper1@example.com', 'ORDER001', '2024-01-15', 'WAY123456', 'Product A', 'SKU001', '2', '500', 'delivered', '2024-01-20', '', 'Delhivery'],
      ['dropshipper1@example.com', 'ORDER002', '2024-01-16', 'WAY123457', 'Product B', 'SKU002', '1', '300', 'delivered', '2024-01-21', '', 'BlueDart'],
      ['dropshipper2@example.com', 'ORDER003', '2024-01-17', 'WAY123458', 'Product C', 'SKU003', '3', '750', 'shipped', '', '', 'DTDC'],
      ['dropshipper2@example.com', 'ORDER004', '2024-01-18', 'WAY123459', 'Product D', 'SKU004', '1', '200', 'rts', '', '2024-01-25', 'Ecom Express'],
    ];

    // Convert to CSV and download
    const csvContent = sampleData.map(row => row.join(',')).join('\n');
    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'sample_orders_template.csv';
    a.click();
    window.URL.revokeObjectURL(url);
  };

  const downloadProductPricesTemplate = () => {
    const priceData = [
      ['Dropshipper Email', 'Product UID', 'Product Name', 'SKU', 'Product Cost Per Unit', 'Currency'],
      ['dropshipper1@example.com', 'SKU001', 'Product A', 'SKU001', '100', 'INR'],
      ['dropshipper1@example.com', 'SKU002', 'Product B', 'SKU002', '150', 'INR'],
      ['dropshipper2@example.com', 'SKU003', 'Product C', 'SKU003', '200', 'INR'],
    ];

    const csvContent = priceData.map(row => row.join(',')).join('\n');
    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'product_prices_template.csv';
    a.click();
    window.URL.revokeObjectURL(url);
  };

  const downloadAllSettings = async () => {
    try {
      const response = await fetch('/api/export-settings', {
        method: 'GET',
      });

      if (!response.ok) throw new Error('Failed to download settings');

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'all_settings.xlsx';
      a.click();
      window.URL.revokeObjectURL(url);

      toast({
        title: "Download completed",
        description: "All settings exported to Excel file.",
      });
    } catch (error) {
      toast({
        title: "Download failed",
        description: "Could not download settings file.",
        variant: "destructive",
      });
    }
  };

  const downloadShippingRatesTemplate = () => {
    const rateData = [
      ['Shipping Provider', 'Shipping Rate Per Order', 'Currency'],
      ['Delhivery', '45', 'INR'],
      ['BlueDart', '50', 'INR'],
      ['DTDC', '40', 'INR'],
      ['Ecom Express', '42', 'INR'],
    ];

    const csvContent = rateData.map(row => row.join(',')).join('\n');
    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'shipping_rates_template.csv';
    a.click();
    window.URL.revokeObjectURL(url);
  };

  return (
    <Card className="mb-6 border-blue-200 bg-blue-50">
      <CardHeader>
        <CardTitle className="flex items-center gap-2 text-blue-800">
          <Settings className="h-5 w-5" />
          Quick Setup Guide
        </CardTitle>
        <CardDescription className="text-blue-700">
          Get started quickly with ready-made templates and default settings
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-4">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="space-y-3">
            <h3 className="font-medium text-blue-800">1. Setup Default Settings</h3>
            <p className="text-sm text-blue-700">
              Add common shipping providers and sample product prices to get started
            </p>
            <Button 
              onClick={handleSetupDefaults} 
              disabled={isSettingUp || setupDefaultsMutation.isPending}
              className="w-full"
              data-testid="button-setup-defaults"
            >
              {isSettingUp ? 'Setting up...' : 'Create Default Settings'}
            </Button>
          </div>

          <div className="space-y-3">
            <h3 className="font-medium text-blue-800">2. Download Templates</h3>
            <p className="text-sm text-blue-700">
              Download ready-made Excel/CSV templates with sample data
            </p>
            <div className="space-y-2">
              <Button 
                onClick={downloadAllSettings}
                className="w-full bg-green-600 hover:bg-green-700"
                data-testid="button-download-all-settings"
              >
                <Download className="h-4 w-4 mr-2" />
                Download All Settings Excel
              </Button>
              <Button 
                variant="outline" 
                onClick={downloadSampleExcel}
                className="w-full"
                data-testid="button-download-orders-template"
              >
                <FileSpreadsheet className="h-4 w-4 mr-2" />
                Orders Template
              </Button>
              <Button 
                variant="outline" 
                onClick={downloadProductPricesTemplate}
                className="w-full"
                data-testid="button-download-prices-template"
              >
                <Download className="h-4 w-4 mr-2" />
                Product Prices Template
              </Button>
            </div>
          </div>
        </div>

        <div className="mt-4 p-3 bg-blue-100 rounded-lg">
          <h4 className="font-medium text-blue-800 mb-2">How to use:</h4>
          <ol className="text-sm text-blue-700 space-y-1">
            <li>1. Click "Create Default Settings" to add common shipping providers</li>
            <li>2. Download the templates and fill them with your data</li>
            <li>3. Upload the completed Excel/CSV files using the upload section</li>
            <li>4. Use the Settings dialog to add/edit product prices and shipping rates</li>
          </ol>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/rts-rto-reconcilia.content:
import React, { useState, useEffect } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Textarea } from '@/components/ui/textarea';
import { Badge } from '@/components/ui/badge';
import { useToast } from '@/hooks/use-toast';
import { AlertTriangle, CheckCircle, Clock, Search, RefreshCw, Calendar } from 'lucide-react';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';

interface PendingRtsRtoOrder {
  orderId: string;
  waybill: string | null;
  dropshipperEmail: string;
  productUid: string;
  productName: string;
  status: string;
  rtsRtoDate: Date | null;
  codAmount: string;
  originalPaymentStatus?: string;
}

interface RtsRtoReconciliation {
  id: string;
  orderId: string;
  waybill: string | null;
  dropshipperEmail: string;
  productUid: string;
  originalPayoutId?: string | null;
  originalPaidAmount: string;
  reversalAmount: string;
  rtsRtoStatus: string;
  rtsRtoDate: Date;
  reconciledOn: Date;
  reconciledBy?: string | null;
  notes?: string | null;
  status: string;
}

interface AutoDetectSuggestion {
  orderId: string;
  waybill: string | null;
  dropshipperEmail: string;
  productUid: string;
  suggestedReversalAmount: number;
  originalPaidAmount: number;
  rtsRtoStatus: string;
  confidence: 'high' | 'medium' | 'low';
  reason: string;
  previousStatus?: string;
  statusChangeDetected: boolean;
}

export function RtsRtoReconciliation() {
  const [selectedDropshipper, setSelectedDropshipper] = useState<string>('all');
  const [dateRange, setDateRange] = useState({
    from: '',
    to: ''
  });
  const [reconciliationForm, setReconciliationForm] = useState({
    orderId: '',
    waybill: '',
    dropshipperEmail: '',
    productUid: '',
    originalPaidAmount: 0,
    reversalAmount: 0,
    rtsRtoStatus: 'RTS' as 'RTS' | 'RTO' | 'RTO-Dispatched',
    rtsRtoDate: '',
    notes: '',
    reconciledBy: ''
  });

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch dropshippers for filter
  const { data: dropshippers = [] } = useQuery<string[]>({
    queryKey: ['/api/dropshippers']
  });

  // Fetch pending RTS/RTO orders
  const { data: pendingOrders = [], isLoading: pendingLoading, refetch: refetchPending } = useQuery<PendingRtsRtoOrder[]>({
    queryKey: ['/api/rts-rto/pending', selectedDropshipper],
    queryFn: async () => {
      const url = selectedDropshipper && selectedDropshipper !== 'all' ? `/api/rts-rto/pending?dropshipperEmail=${selectedDropshipper}` : '/api/rts-rto/pending';
      const response = await fetch(url);
      if (!response.ok) throw new Error('Failed to fetch pending orders');
      return response.json();
    }
  });

  // Fetch RTS/RTO reconciliation history
  const { data: reconciliationHistory = [], isLoading: historyLoading, refetch: refetchHistory } = useQuery<RtsRtoReconciliation[]>({
    queryKey: ['/api/rts-rto/history', selectedDropshipper, dateRange.from, dateRange.to],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (selectedDropshipper && selectedDropshipper !== 'all') params.append('dropshipperEmail', selectedDropshipper);
      if (dateRange.from) params.append('from', dateRange.from);
      if (dateRange.to) params.append('to', dateRange.to);
      const response = await fetch(`/api/rts-rto/history?${params.toString()}`);
      if (!response.ok) throw new Error('Failed to fetch history');
      return response.json();
    }
  });

  // Auto-detect reconciliation suggestions
  const { data: suggestions = [], isLoading: suggestionsLoading, refetch: refetchSuggestions } = useQuery<AutoDetectSuggestion[]>({
    queryKey: ['/api/rts-rto/auto-detect', selectedDropshipper, dateRange.from, dateRange.to],
    queryFn: async () => {
      if (!dateRange.from || !dateRange.to) return [];
      const response = await fetch('/api/rts-rto/auto-detect', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          orderDateFrom: dateRange.from,
          orderDateTo: dateRange.to,
          dropshipperEmail: selectedDropshipper && selectedDropshipper !== 'all' ? selectedDropshipper : undefined
        })
      });
      if (!response.ok) throw new Error('Failed to auto-detect');
      return response.json();
    },
    enabled: !!(dateRange.from && dateRange.to)
  });

  // Process reconciliation mutation
  const reconcileMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await fetch('/api/rts-rto/reconcile', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data)
      });
      if (!response.ok) throw new Error('Failed to process reconciliation');
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Reconciliation Processed",
        description: "RTS/RTO reconciliation has been successfully processed."
      });
      queryClient.invalidateQueries({ queryKey: ['/api/rts-rto/pending'] });
      queryClient.invalidateQueries({ queryKey: ['/api/rts-rto/history'] });
      setReconciliationForm({
        orderId: '',
        waybill: '',
        dropshipperEmail: '',
        productUid: '',
        originalPaidAmount: 0,
        reversalAmount: 0,
        rtsRtoStatus: 'RTS',
        rtsRtoDate: '',
        notes: '',
        reconciledBy: ''
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to process reconciliation",
        variant: "destructive"
      });
    }
  });

  const handleProcessReconciliation = (e: React.FormEvent) => {
    e.preventDefault();
    if (!reconciliationForm.orderId || !reconciliationForm.dropshipperEmail) {
      toast({
        title: "Validation Error",
        description: "Order ID and Dropshipper Email are required",
        variant: "destructive"
      });
      return;
    }

    reconcileMutation.mutate(reconciliationForm);
  };

  const fillFormFromSuggestion = (suggestion: AutoDetectSuggestion) => {
    setReconciliationForm({
      orderId: suggestion.orderId,
      waybill: suggestion.waybill || '',
      dropshipperEmail: suggestion.dropshipperEmail,
      productUid: suggestion.productUid,
      originalPaidAmount: suggestion.originalPaidAmount,
      reversalAmount: suggestion.suggestedReversalAmount,
      rtsRtoStatus: suggestion.rtsRtoStatus as 'RTS' | 'RTO' | 'RTO-Dispatched',
      rtsRtoDate: new Date().toISOString().split('T')[0],
      notes: suggestion.reason,
      reconciledBy: ''
    });
  };

  const fillFormFromPendingOrder = (order: PendingRtsRtoOrder) => {
    setReconciliationForm({
      orderId: order.orderId,
      waybill: order.waybill || '',
      dropshipperEmail: order.dropshipperEmail,
      productUid: order.productUid,
      originalPaidAmount: 0,
      reversalAmount: parseFloat(order.codAmount) || 0,
      rtsRtoStatus: order.status as 'RTS' | 'RTO' | 'RTO-Dispatched',
      rtsRtoDate: order.rtsRtoDate ? new Date(order.rtsRtoDate).toISOString().split('T')[0] : new Date().toISOString().split('T')[0],
      notes: `Auto-filled from ${order.status} order`,
      reconciledBy: ''
    });
  };

  const getConfidenceBadge = (confidence: string) => {
    const colors = {
      high: 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200',
      medium: 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200',
      low: 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200'
    };
    return (
      <Badge className={colors[confidence as keyof typeof colors] || colors.low}>
        {confidence.toUpperCase()}
      </Badge>
    );
  };

  const getStatusBadge = (status: string) => {
    const colors = {
      RTS: 'bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-200',
      RTO: 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200',
      'RTO-Dispatched': 'bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200',
      pending: 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200',
      processed: 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200'
    };
    return (
      <Badge className={colors[status as keyof typeof colors] || 'bg-gray-100 text-gray-800'}>
        {status}
      </Badge>
    );
  };

  return (
    <div className="space-y-6" data-testid="rts-rto-reconciliation">
      {/* Filters */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Search className="h-5 w-5" />
            RTS/RTO Reconciliation Filters
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <Label htmlFor="dropshipper-select">Dropshipper (Optional)</Label>
              <Select value={selectedDropshipper} onValueChange={setSelectedDropshipper}>
                <SelectTrigger data-testid="select-dropshipper">
                  <SelectValue placeholder="All Dropshippers" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Dropshippers</SelectItem>
                  {dropshippers.map((email: string) => (
                    <SelectItem key={email} value={email}>{email}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div>
              <Label htmlFor="date-from">Date From</Label>
              <Input
                id="date-from"
                type="date"
                value={dateRange.from}
                onChange={(e) => setDateRange(prev => ({ ...prev, from: e.target.value }))}
                data-testid="input-date-from"
              />
            </div>
            <div>
              <Label htmlFor="date-to">Date To</Label>
              <Input
                id="date-to"
                type="date"
                value={dateRange.to}
                onChange={(e) => setDateRange(prev => ({ ...prev, to: e.target.value }))}
                data-testid="input-date-to"
              />
            </div>
          </div>
        </CardContent>
      </Card>

      <Tabs defaultValue="pending" className="w-full">
        <TabsList className="grid w-full grid-cols-4">
          <TabsTrigger value="pending" data-testid="tab-pending">
            Pending Orders ({pendingOrders.length})
          </TabsTrigger>
          <TabsTrigger value="suggestions" data-testid="tab-suggestions">
            Auto Suggestions ({suggestions.length})
          </TabsTrigger>
          <TabsTrigger value="process" data-testid="tab-process">
            Manual Process
          </TabsTrigger>
          <TabsTrigger value="history" data-testid="tab-history">
            History
          </TabsTrigger>
        </TabsList>

        {/* Pending Orders */}
        <TabsContent value="pending">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center justify-between">
                <span className="flex items-center gap-2">
                  <Clock className="h-5 w-5" />
                  Pending RTS/RTO Orders
                </span>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => refetchPending()}
                  disabled={pendingLoading}
                  data-testid="button-refresh-pending"
                >
                  <RefreshCw className={`h-4 w-4 ${pendingLoading ? 'animate-spin' : ''}`} />
                  Refresh
                </Button>
              </CardTitle>
            </CardHeader>
            <CardContent>
              {pendingLoading ? (
                <div className="text-center py-8">Loading pending orders...</div>
              ) : pendingOrders.length === 0 ? (
                <div className="text-center py-8 text-muted-foreground">
                  No pending RTS/RTO orders found
                </div>
              ) : (
                <div className="space-y-4">
                  {pendingOrders.map((order: PendingRtsRtoOrder) => (
                    <div
                      key={order.orderId}
                      className="border rounded-lg p-4 hover:bg-muted/50 transition-colors"
                      data-testid={`pending-order-${order.orderId}`}
                    >
                      <div className="flex items-center justify-between">
                        <div className="space-y-2">
                          <div className="flex items-center gap-2">
                            <span className="font-medium">Order: {order.orderId}</span>
                            {getStatusBadge(order.status)}
                          </div>
                          <div className="text-sm text-muted-foreground">
                            <div>Product: {order.productName}</div>
                            <div>Dropshipper: {order.dropshipperEmail}</div>
                            <div>COD Amount: â‚¹{order.codAmount}</div>
                            {order.rtsRtoDate && (
                              <div>RTS/RTO Date: {new Date(order.rtsRtoDate).toLocaleDateString()}</div>
                            )}
                          </div>
                        </div>
                        <Button
                          variant="outline"
                          onClick={() => fillFormFromPendingOrder(order)}
                          data-testid={`button-process-${order.orderId}`}
                        >
                          Process Reconciliation
                        </Button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        {/* Auto Suggestions */}
        <TabsContent value="suggestions">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center justify-between">
                <span className="flex items-center gap-2">
                  <AlertTriangle className="h-5 w-5" />
                  Auto-Detection Suggestions
                </span>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => refetchSuggestions()}
                  disabled={suggestionsLoading || !dateRange.from || !dateRange.to}
                  data-testid="button-refresh-suggestions"
                >
                  <RefreshCw className={`h-4 w-4 ${suggestionsLoading ? 'animate-spin' : ''}`} />
                  Auto-Detect
                </Button>
              </CardTitle>
            </CardHeader>
            <CardContent>
              {!dateRange.from || !dateRange.to ? (
                <div className="text-center py-8 text-muted-foreground">
                  Please select date range to auto-detect reconciliations
                </div>
              ) : suggestionsLoading ? (
                <div className="text-center py-8">Auto-detecting reconciliations...</div>
              ) : suggestions.length === 0 ? (
                <div className="text-center py-8 text-muted-foreground">
                  No reconciliation suggestions found for the selected date range
                </div>
              ) : (
                <div className="space-y-4">
                  {suggestions.map((suggestion: AutoDetectSuggestion) => (
                    <div
                      key={suggestion.orderId}
                      className={`border rounded-lg p-4 hover:bg-muted/50 transition-colors ${
                        suggestion.statusChangeDetected ? 'border-orange-200 bg-orange-50 dark:bg-orange-950' : ''
                      }`}
                      data-testid={`suggestion-${suggestion.orderId}`}
                    >
                      <div className="flex items-center justify-between">
                        <div className="space-y-2">
                          <div className="flex items-center gap-2">
                            <span className="font-medium">Order: {suggestion.orderId}</span>
                            {getStatusBadge(suggestion.rtsRtoStatus)}
                            {getConfidenceBadge(suggestion.confidence)}
                            {suggestion.statusChangeDetected && (
                              <Badge className="bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-200">
                                Status Changed
                              </Badge>
                            )}
                          </div>
                          <div className="text-sm text-muted-foreground">
                            <div>Dropshipper: {suggestion.dropshipperEmail}</div>
                            {suggestion.previousStatus && (
                              <div className="font-medium text-orange-700 dark:text-orange-300">
                                Previous Status: {suggestion.previousStatus} â†’ {suggestion.rtsRtoStatus}
                              </div>
                            )}
                            <div>Suggested Reversal: â‚¹{suggestion.suggestedReversalAmount.toFixed(2)}</div>
                            <div>Original Paid: â‚¹{suggestion.originalPaidAmount.toFixed(2)}</div>
                            <div className="mt-1 p-2 bg-gray-50 dark:bg-gray-800 rounded text-xs">
                              <strong>Reason:</strong> {suggestion.reason}
                            </div>
                          </div>
                        </div>
                        <Button
                          variant={suggestion.statusChangeDetected ? "default" : "outline"}
                          onClick={() => fillFormFromSuggestion(suggestion)}
                          data-testid={`button-apply-suggestion-${suggestion.orderId}`}
                          className={suggestion.statusChangeDetected ? "bg-orange-500 hover:bg-orange-600 text-white" : ""}
                        >
                          {suggestion.statusChangeDetected ? "Process Now" : "Apply Suggestion"}
                        </Button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        {/* Manual Processing Form */}
        <TabsContent value="process">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <CheckCircle className="h-5 w-5" />
                Manual RTS/RTO Reconciliation
              </CardTitle>
            </CardHeader>
            <CardContent>
              <form onSubmit={handleProcessReconciliation} className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="order-id">Order ID *</Label>
                    <Input
                      id="order-id"
                      value={reconciliationForm.orderId}
                      onChange={(e) => setReconciliationForm(prev => ({ ...prev, orderId: e.target.value }))}
                      required
                      data-testid="input-order-id"
                    />
                  </div>
                  <div>
                    <Label htmlFor="waybill">Waybill</Label>
                    <Input
                      id="waybill"
                      value={reconciliationForm.waybill}
                      onChange={(e) => setReconciliationForm(prev => ({ ...prev, waybill: e.target.value }))}
                      data-testid="input-waybill"
                    />
                  </div>
                  <div>
                    <Label htmlFor="dropshipper-email">Dropshipper Email *</Label>
                    <Input
                      id="dropshipper-email"
                      type="email"
                      value={reconciliationForm.dropshipperEmail}
                      onChange={(e) => setReconciliationForm(prev => ({ ...prev, dropshipperEmail: e.target.value }))}
                      required
                      data-testid="input-dropshipper-email"
                    />
                  </div>
                  <div>
                    <Label htmlFor="product-uid">Product UID</Label>
                    <Input
                      id="product-uid"
                      value={reconciliationForm.productUid}
                      onChange={(e) => setReconciliationForm(prev => ({ ...prev, productUid: e.target.value }))}
                      data-testid="input-product-uid"
                    />
                  </div>
                  <div>
                    <Label htmlFor="original-paid-amount">Original Paid Amount (â‚¹)</Label>
                    <Input
                      id="original-paid-amount"
                      type="number"
                      step="0.01"
                      value={reconciliationForm.originalPaidAmount}
                      onChange={(e) => setReconciliationForm(prev => ({ ...prev, originalPaidAmount: parseFloat(e.target.value) || 0 }))}
                      data-testid="input-original-paid-amount"
                    />
                  </div>
                  <div>
                    <Label htmlFor="reversal-amount">Reversal Amount (â‚¹) *</Label>
                    <Input
                      id="reversal-amount"
                      type="number"
                      step="0.01"
                      value={reconciliationForm.reversalAmount}
                      onChange={(e) => setReconciliationForm(prev => ({ ...prev, reversalAmount: parseFloat(e.target.value) || 0 }))}
                      required
                      data-testid="input-reversal-amount"
                    />
                  </div>
                  <div>
                    <Label htmlFor="rts-rto-status">RTS/RTO Status</Label>
                    <Select
                      value={reconciliationForm.rtsRtoStatus}
                      onValueChange={(value) => setReconciliationForm(prev => ({ ...prev, rtsRtoStatus: value as 'RTS' | 'RTO' | 'RTO-Dispatched' }))}
                    >
                      <SelectTrigger data-testid="select-rts-rto-status">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="RTS">RTS</SelectItem>
                        <SelectItem value="RTO">RTO</SelectItem>
                        <SelectItem value="RTO-Dispatched">RTO-Dispatched</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                  <div>
                    <Label htmlFor="rts-rto-date">RTS/RTO Date</Label>
                    <Input
                      id="rts-rto-date"
                      type="date"
                      value={reconciliationForm.rtsRtoDate}
                      onChange={(e) => setReconciliationForm(prev => ({ ...prev, rtsRtoDate: e.target.value }))}
                      data-testid="input-rts-rto-date"
                    />
                  </div>
                </div>
                <div>
                  <Label htmlFor="reconciled-by">Reconciled By</Label>
                  <Input
                    id="reconciled-by"
                    value={reconciliationForm.reconciledBy}
                    onChange={(e) => setReconciliationForm(prev => ({ ...prev, reconciledBy: e.target.value }))}
                    placeholder="Enter your name or ID"
                    data-testid="input-reconciled-by"
                  />
                </div>
                <div>
                  <Label htmlFor="notes">Notes</Label>
                  <Textarea
                    id="notes"
                    value={reconciliationForm.notes}
                    onChange={(e) => setReconciliationForm(prev => ({ ...prev, notes: e.target.value }))}
                    placeholder="Add any relevant notes about this reconciliation..."
                    data-testid="textarea-notes"
                  />
                </div>
                <Button
                  type="submit"
                  disabled={reconcileMutation.isPending}
                  className="w-full"
                  data-testid="button-submit-reconciliation"
                >
                  {reconcileMutation.isPending ? 'Processing...' : 'Process Reconciliation'}
                </Button>
              </form>
            </CardContent>
          </Card>
        </TabsContent>

        {/* History */}
        <TabsContent value="history">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center justify-between">
                <span className="flex items-center gap-2">
                  <Calendar className="h-5 w-5" />
                  Reconciliation History
                </span>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => refetchHistory()}
                  disabled={historyLoading}
                  data-testid="button-refresh-history"
                >
                  <RefreshCw className={`h-4 w-4 ${historyLoading ? 'animate-spin' : ''}`} />
                  Refresh
                </Button>
              </CardTitle>
            </CardHeader>
            <CardContent>
              {historyLoading ? (
                <div className="text-center py-8">Loading reconciliation history...</div>
              ) : reconciliationHistory.length === 0 ? (
                <div className="text-center py-8 text-muted-foreground">
                  No reconciliation history found
                </div>
              ) : (
                <div className="space-y-4">
                  {reconciliationHistory.map((record: RtsRtoReconciliation) => (
                    <div
                      key={record.id}
                      className="border rounded-lg p-4"
                      data-testid={`history-record-${record.id}`}
                    >
                      <div className="flex items-center justify-between mb-2">
                        <div className="flex items-center gap-2">
                          <span className="font-medium">Order: {record.orderId}</span>
                          {getStatusBadge(record.rtsRtoStatus)}
                          {getStatusBadge(record.status)}
                        </div>
                        <div className="text-sm text-muted-foreground">
                          {new Date(record.reconciledOn).toLocaleDateString()}
                        </div>
                      </div>
                      <div className="grid grid-cols-2 gap-4 text-sm">
                        <div>
                          <div><strong>Dropshipper:</strong> {record.dropshipperEmail}</div>
                          <div><strong>Original Amount:</strong> â‚¹{record.originalPaidAmount}</div>
                          <div><strong>Reversal Amount:</strong> â‚¹{record.reversalAmount}</div>
                        </div>
                        <div>
                          <div><strong>RTS/RTO Date:</strong> {new Date(record.rtsRtoDate).toLocaleDateString()}</div>
                          {record.reconciledBy && <div><strong>Reconciled By:</strong> {record.reconciledBy}</div>}
                          {record.notes && <div><strong>Notes:</strong> {record.notes}</div>}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/components/settings-dialog.ts.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Plus, Upload, Edit, Trash2, X } from "lucide-react";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import type { ProductPrice, ShippingRate } from "@shared/schema";

interface SettingsDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  missingData?: {
    missingPrices: Array<{ dropshipperEmail: string; productUid: string; productName: string; sku: string | null }>;
    missingRates: string[];
  };
  onSettingsUpdated: () => void;
}

interface EditingPrice {
  id?: string;
  dropshipperEmail: string;
  productUid: string;
  productName: string;
  sku: string;
  productCostPerUnit: string;
  currency: string;
}

interface EditingRate {
  id?: string;
  shippingProvider: string;
  shippingRatePerOrder: string;
  currency: string;
}

export default function SettingsDialog({ 
  open, 
  onOpenChange, 
  missingData,
  onSettingsUpdated 
}: SettingsDialogProps) {
  const [activeTab, setActiveTab] = useState("product-prices");
  const [editingPrice, setEditingPrice] = useState<EditingPrice | null>(null);
  const [editingRate, setEditingRate] = useState<EditingRate | null>(null);
  const { toast } = useToast();

  // Fetch data
  const { data: productPrices = [], refetch: refetchPrices } = useQuery<ProductPrice[]>({
    queryKey: ['/api/product-prices'],
    enabled: open,
  });

  const { data: shippingRates = [], refetch: refetchRates } = useQuery<ShippingRate[]>({
    queryKey: ['/api/shipping-rates'],
    enabled: open,
  });

  // Mutations
  const saveProductPriceMutation = useMutation({
    mutationFn: async (price: Omit<EditingPrice, 'id'>) => {
      const response = await apiRequest('POST', '/api/product-prices', {
        ...price,
        productCostPerUnit: parseFloat(price.productCostPerUnit),
      });
      return response.json();
    },
    onSuccess: () => {
      toast({ title: "Product price saved successfully" });
      refetchPrices();
      queryClient.invalidateQueries({ queryKey: ['/api/missing-data'] });
      setEditingPrice(null);
      onSettingsUpdated();
    },
    onError: () => {
      toast({ 
        title: "Failed to save product price", 
        variant: "destructive" 
      });
    },
  });

  const deleteProductPriceMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest('DELETE', `/api/product-prices/${id}`);
    },
    onSuccess: () => {
      toast({ title: "Product price deleted successfully" });
      refetchPrices();
      onSettingsUpdated();
    },
    onError: () => {
      toast({ 
        title: "Failed to delete product price", 
        variant: "destructive" 
      });
    },
  });

  const saveShippingRateMutation = useMutation({
    mutationFn: async (rate: Omit<EditingRate, 'id'>) => {
      const response = await apiRequest('POST', '/api/shipping-rates', {
        ...rate,
        shippingRatePerOrder: parseFloat(rate.shippingRatePerOrder),
      });
      return response.json();
    },
    onSuccess: () => {
      toast({ title: "Shipping rate saved successfully" });
      refetchRates();
      queryClient.invalidateQueries({ queryKey: ['/api/missing-data'] });
      setEditingRate(null);
      onSettingsUpdated();
    },
    onError: () => {
      toast({ 
        title: "Failed to save shipping rate", 
        variant: "destructive" 
      });
    },
  });

  const deleteShippingRateMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest('DELETE', `/api/shipping-rates/${id}`);
    },
    onSuccess: () => {
      toast({ title: "Shipping rate deleted successfully" });
      refetchRates();
      onSettingsUpdated();
    },
    onError: () => {
      toast({ 
        title: "Failed to delete shipping rate", 
        variant: "destructive" 
      });
    },
  });

  const handleAddMissingPrices = () => {
    if (!missingData?.missingPrices) return;

    missingData.missingPrices.forEach(item => {
      const newPrice: EditingPrice = {
        dropshipperEmail: item.dropshipperEmail,
        productUid: item.productUid,
        productName: item.productName,
        sku: item.sku || "",
        productCostPerUnit: "0.00",
        currency: "INR",
      };
      saveProductPriceMutation.mutate(newPrice);
    });
  };

  const handleAddMissingRates = () => {
    if (!missingData?.missingRates) return;

    missingData.missingRates.forEach(provider => {
      const newRate: EditingRate = {
        shippingProvider: provider,
        shippingRatePerOrder: "0.00",
        currency: "INR",
      };
      saveShippingRateMutation.mutate(newRate);
    });
  };

  const formatCurrency = (amount: string) => {
    const num = parseFloat(amount);
    return isNaN(num) ? "â‚¹0.00" : new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
    }).format(num);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-6xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center justify-between" data-testid="text-settings-title">
            Settings
            <Button 
              variant="ghost" 
              size="icon" 
              onClick={() => onOpenChange(false)}
              data-testid="button-close-settings"
            >
              <X className="h-4 w-4" />
            </Button>
          </DialogTitle>
          <DialogDescription>
            Manage product prices and shipping rates for payout calculations
          </DialogDescription>
        </DialogHeader>

        <Tabs value={activeTab} onValueChange={setActiveTab}>
          <TabsList className="grid w-full grid-cols-2">
            <TabsTrigger value="product-prices" data-testid="tab-product-prices">
              Product Prices
            </TabsTrigger>
            <TabsTrigger value="shipping-rates" data-testid="tab-shipping-rates">
              Shipping Rates
            </TabsTrigger>
          </TabsList>

          {/* Product Prices Tab */}
          <TabsContent value="product-prices" className="space-y-4">
            <div className="flex justify-between items-center">
              <h4 className="text-md font-medium text-gray-900">Product Price Management</h4>
              <div className="flex space-x-2">
                {missingData && missingData.missingPrices.length > 0 && (
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={handleAddMissingPrices}
                    data-testid="button-add-missing-prices"
                  >
                    Add Missing ({missingData.missingPrices.length})
                  </Button>
                )}
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => setEditingPrice({
                    dropshipperEmail: "",
                    productUid: "",
                    productName: "",
                    sku: "",
                    productCostPerUnit: "0.00",
                    currency: "INR",
                  })}
                  data-testid="button-add-price-row"
                >
                  <Plus className="mr-2 h-4 w-4" />
                  Add Row
                </Button>
              </div>
            </div>

            {/* Add/Edit Form */}
            {editingPrice && (
              <div className="border rounded-lg p-4 bg-gray-50">
                <h5 className="font-medium mb-3">
                  {editingPrice.id ? "Edit Product Price" : "Add Product Price"}
                </h5>
                <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
                  <div>
                    <Label htmlFor="edit-dropshipper-email">Dropshipper Email</Label>
                    <Input
                      id="edit-dropshipper-email"
                      value={editingPrice.dropshipperEmail}
                      onChange={(e) => setEditingPrice({...editingPrice, dropshipperEmail: e.target.value})}
                      placeholder="dropshipper@example.com"
                      data-testid="input-edit-dropshipper-email"
                    />
                  </div>
                  <div>
                    <Label htmlFor="edit-product-uid">Product UID</Label>
                    <Input
                      id="edit-product-uid"
                      value={editingPrice.productUid}
                      onChange={(e) => setEditingPrice({...editingPrice, productUid: e.target.value})}
                      placeholder="PRD-001"
                      data-testid="input-edit-product-uid"
                    />
                  </div>
                  <div>
                    <Label htmlFor="edit-product-name">Product Name</Label>
                    <Input
                      id="edit-product-name"
                      value={editingPrice.productName}
                      onChange={(e) => setEditingPrice({...editingPrice, productName: e.target.value})}
                      placeholder="Product Name"
                      data-testid="input-edit-product-name"
                    />
                  </div>
                  <div>
                    <Label htmlFor="edit-sku">SKU</Label>
                    <Input
                      id="edit-sku"
                      value={editingPrice.sku}
                      onChange={(e) => setEditingPrice({...editingPrice, sku: e.target.value})}
                      placeholder="SKU-001"
                      data-testid="input-edit-sku"
                    />
                  </div>
                  <div>
                    <Label htmlFor="edit-cost-per-unit">Cost Per Unit</Label>
                    <Input
                      id="edit-cost-per-unit"
                      type="number"
                      step="0.01"
                      min="0"
                      value={editingPrice.productCostPerUnit}
                      onChange={(e) => setEditingPrice({...editingPrice, productCostPerUnit: e.target.value})}
                      placeholder="0.00"
                      data-testid="input-edit-cost-per-unit"
                    />
                  </div>
                  <div>
                    <Label htmlFor="edit-currency">Currency</Label>
                    <Input
                      id="edit-currency"
                      value={editingPrice.currency}
                      onChange={(e) => setEditingPrice({...editingPrice, currency: e.target.value})}
                      placeholder="INR"
                      data-testid="input-edit-currency"
                    />
                  </div>
                </div>
                <div className="flex justify-end space-x-2 mt-4">
                  <Button
                    variant="outline"
                    onClick={() => setEditingPrice(null)}
                    data-testid="button-cancel-price-edit"
                  >
                    Cancel
                  </Button>
                  <Button
                    onClick={() => saveProductPriceMutation.mutate(editingPrice)}
                    disabled={saveProductPriceMutation.isPending}
                    data-testid="button-save-price"
                  >
                    {saveProductPriceMutation.isPending ? "Saving..." : "Save"}
                  </Button>
                </div>
              </div>
            )}

            {/* Table */}
            <div className="border rounded-lg overflow-hidden">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Dropshipper Email</TableHead>
                    <TableHead>Product UID</TableHead>
                    <TableHead>Product Name</TableHead>
                    <TableHead>SKU</TableHead>
                    <TableHead>Cost Per Unit</TableHead>
                    <TableHead>Currency</TableHead>
                    <TableHead>Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {productPrices.map((price) => (
                    <TableRow key={price.id} data-testid={`row-price-${price.id}`}>
                      <TableCell className="font-medium">{price.dropshipperEmail}</TableCell>
                      <TableCell>{price.productUid}</TableCell>
                      <TableCell>{price.productName}</TableCell>
                      <TableCell>{price.sku || "-"}</TableCell>
                      <TableCell>{formatCurrency(price.productCostPerUnit)}</TableCell>
                      <TableCell>{price.currency}</TableCell>
                      <TableCell>
                        <div className="flex space-x-2">
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => setEditingPrice({
                              id: price.id,
                              dropshipperEmail: price.dropshipperEmail,
                              productUid: price.productUid,
                              productName: price.productName,
                              sku: price.sku || "",
                              productCostPerUnit: price.productCostPerUnit,
                              currency: price.currency,
                            })}
                            data-testid={`button-edit-price-${price.id}`}
                          >
                            <Edit className="h-4 w-4" />
                          </Button>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => deleteProductPriceMutation.mutate(price.id)}
                            disabled={deleteProductPriceMutation.isPending}
                            data-testid={`button-delete-price-${price.id}`}
                          >
                            <Trash2 className="h-4 w-4 text-red-500" />
                          </Button>
                        </div>
                      </TableCell>
                    </TableRow>
                  ))}
                  {productPrices.length === 0 && (
                    <TableRow>
                      <TableCell colSpan={7} className="text-center py-8 text-gray-500">
                        No product prices configured. Click "Add Row" to get started.
                      </TableCell>
                    </TableRow>
                  )}
                </TableBody>
              </Table>
            </div>
          </TabsContent>

          {/* Shipping Rates Tab */}
          <TabsContent value="shipping-rates" className="space-y-4">
            <div className="flex justify-between items-center">
              <h4 className="text-md font-medium text-gray-900">Shipping Rate Management</h4>
              <div className="flex space-x-2">
                {missingData && missingData.missingRates.length > 0 && (
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={handleAddMissingRates}
                    data-testid="button-add-missing-rates"
                  >
                    Add Missing ({missingData.missingRates.length})
                  </Button>
                )}
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => setEditingRate({
                    shippingProvider: "",
                    shippingRatePerOrder: "0.00",
                    currency: "INR",
                  })}
                  data-testid="button-add-rate-row"
                >
                  <Plus className="mr-2 h-4 w-4" />
                  Add Row
                </Button>
              </div>
            </div>

            {/* Add/Edit Form */}
            {editingRate && (
              <div className="border rounded-lg p-4 bg-gray-50">
                <h5 className="font-medium mb-3">
                  {editingRate.id ? "Edit Shipping Rate" : "Add Shipping Rate"}
                </h5>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div>
                    <Label htmlFor="edit-shipping-provider">Shipping Provider</Label>
                    <Input
                      id="edit-shipping-provider"
                      value={editingRate.shippingProvider}
                      onChange={(e) => setEditingRate({...editingRate, shippingProvider: e.target.value})}
                      placeholder="Blue Dart"
                      data-testid="input-edit-shipping-provider"
                    />
                  </div>
                  <div>
                    <Label htmlFor="edit-rate-per-order">Rate Per Order</Label>
                    <Input
                      id="edit-rate-per-order"
                      type="number"
                      step="0.01"
                      min="0"
                      value={editingRate.shippingRatePerOrder}
                      onChange={(e) => setEditingRate({...editingRate, shippingRatePerOrder: e.target.value})}
                      placeholder="0.00"
                      data-testid="input-edit-rate-per-order"
                    />
                  </div>
                  <div>
                    <Label htmlFor="edit-rate-currency">Currency</Label>
                    <Input
                      id="edit-rate-currency"
                      value={editingRate.currency}
                      onChange={(e) => setEditingRate({...editingRate, currency: e.target.value})}
                      placeholder="INR"
                      data-testid="input-edit-rate-currency"
                    />
                  </div>
                </div>
                <div className="flex justify-end space-x-2 mt-4">
                  <Button
                    variant="outline"
                    onClick={() => setEditingRate(null)}
                    data-testid="button-cancel-rate-edit"
                  >
                    Cancel
                  </Button>
                  <Button
                    onClick={() => saveShippingRateMutation.mutate(editingRate)}
                    disabled={saveShippingRateMutation.isPending}
                    data-testid="button-save-rate"
                  >
                    {saveShippingRateMutation.isPending ? "Saving..." : "Save"}
                  </Button>
                </div>
              </div>
            )}

            {/* Table */}
            <div className="border rounded-lg overflow-hidden">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Shipping Provider</TableHead>
                    <TableHead>Rate Per Order</TableHead>
                    <TableHead>Currency</TableHead>
                    <TableHead>Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {shippingRates.map((rate) => (
                    <TableRow key={rate.id} data-testid={`row-rate-${rate.id}`}>
                      <TableCell className="font-medium">{rate.shippingProvider}</TableCell>
                      <TableCell>{formatCurrency(rate.shippingRatePerOrder)}</TableCell>
                      <TableCell>{rate.currency}</TableCell>
                      <TableCell>
                        <div className="flex space-x-2">
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => setEditingRate({
                              id: rate.id,
                              shippingProvider: rate.shippingProvider,
                              shippingRatePerOrder: rate.shippingRatePerOrder,
                              currency: rate.currency,
                            })}
                            data-testid={`button-edit-rate-${rate.id}`}
                          >
                            <Edit className="h-4 w-4" />
                          </Button>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => deleteShippingRateMutation.mutate(rate.id)}
                            disabled={deleteShippingRateMutation.isPending}
                            data-testid={`button-delete-rate-${rate.id}`}
                          >
                            <Trash2 className="h-4 w-4 text-red-500" />
                          </Button>
                        </div>
                      </TableCell>
                    </TableRow>
                  ))}
                  {shippingRates.length === 0 && (
                    <TableRow>
                      <TableCell colSpan={4} className="text-center py-8 text-gray-500">
                        No shipping rates configured. Click "Add Row" to get started.
                      </TableCell>
                    </TableRow>
                  )}
                </TableBody>
              </Table>
            </div>
          </TabsContent>
        </Tabs>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/settings-upload.ts.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { Upload, FileSpreadsheet } from "lucide-react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { useToast } from "@/hooks/use-toast";
import { queryClient } from "@/lib/queryClient";

export default function SettingsUpload() {
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const { toast } = useToast();

  const uploadMutation = useMutation({
    mutationFn: async (file: File) => {
      const formData = new FormData();
      formData.append('file', file);

      const response = await fetch('/api/import-settings', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Upload failed');
      }

      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Settings imported successfully",
        description: `Imported ${data.importedPrices} product prices and ${data.importedRates} shipping rates.`,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/product-prices'] });
      queryClient.invalidateQueries({ queryKey: ['/api/shipping-rates'] });
      queryClient.invalidateQueries({ queryKey: ['/api/transparency/config-summary'] });
      queryClient.invalidateQueries({ queryKey: ['/api/transparency/product-prices'] });
      queryClient.invalidateQueries({ queryKey: ['/api/transparency/shipping-rates'] });
      queryClient.invalidateQueries({ queryKey: ['/api/missing-data'] });
      setSelectedFile(null);
    },
    onError: () => {
      toast({
        title: "Import failed",
        description: "Could not import settings. Please check the file format.",
        variant: "destructive",
      });
    }
  });

  const handleFileSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      setSelectedFile(file);
    }
  };

  const handleUpload = () => {
    if (selectedFile) {
      uploadMutation.mutate(selectedFile);
    }
  };

  return (
    <Card className="mb-6 border-green-200 bg-green-50">
      <CardHeader>
        <CardTitle className="flex items-center gap-2 text-green-800">
          <Upload className="h-5 w-5" />
          Settings Upload
        </CardTitle>
        <CardDescription className="text-green-700">
          Upload Excel file with product prices and shipping rates
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-4">
        <div className="space-y-3">
          <div className="flex items-center space-x-2">
            <Input
              type="file"
              accept=".xlsx,.xls"
              onChange={handleFileSelect}
              className="flex-1"
              data-testid="input-settings-file"
            />
            <Button 
              onClick={handleUpload}
              disabled={!selectedFile || uploadMutation.isPending}
              data-testid="button-upload-settings"
            >
              {uploadMutation.isPending ? 'Uploading...' : 'Upload'}
            </Button>
          </div>

          {selectedFile && (
            <div className="text-sm text-green-700">
              Selected: {selectedFile.name} ({Math.round(selectedFile.size / 1024)} KB)
            </div>
          )}
        </div>

        <div className="p-3 bg-green-100 rounded-lg">
          <h4 className="font-medium text-green-800 mb-2">Excel Format Required:</h4>
          <ul className="text-sm text-green-700 space-y-1">
            <li>â€¢ <strong>Sheet 1:</strong> "Product Prices" - Dropshipper Email, Product UID, Product Name, SKU, Product Weight (kg), Product Cost Per Unit, Currency</li>
            <li>â€¢ <strong>Sheet 2:</strong> "Shipping Rates" - Shipping UID, Product UID, Product Weight (kg), Shipping Provider, Shipping Rate Per Kg, Currency</li>
            <li>â€¢ <strong>Shipping UID Format:</strong> DropshipperEmail+ProductName+Weight+Provider (no separators)</li>
            <li>â€¢ Download template to get proper Excel format with column widths and examples</li>
            <li>â€¢ Delete instruction/example rows before upload - system handles duplicates automatically</li>
          </ul>
        </div>
      </CardContent>
    </Card>
  );
}

