import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from "@/components/ui/collapsible";
import { Input } from "@/components/ui/input";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { Download, FileText, Calendar, User, ChevronDown, Package, Truck, DollarSign, Search, Filter, MoreVertical, Database, Settings } from 'lucide-react';
import { format } from 'date-fns';
import { apiRequest } from "@/lib/queryClient";

interface UploadSession {
  id: string;
  filename: string;
  uploadedAt: string;
  orderCount: number;
  dropshipperEmail?: string;
  productPricesCount?: number;
  shippingRatesCount?: number;
  hasProductPrices?: boolean;
  hasShippingRates?: boolean;
}

export function UploadHistory() {
  const { data: uploads = [], isLoading } = useQuery<UploadSession[]>({
    queryKey: ['/api/transparency/uploads'],
    enabled: true
  });

  const [expandedFiles, setExpandedFiles] = useState<Set<string>>(new Set());
  const [searchTerm, setSearchTerm] = useState('');

  const toggleExpanded = (fileId: string) => {
    const newExpanded = new Set(expandedFiles);
    if (newExpanded.has(fileId)) {
      newExpanded.delete(fileId);
    } else {
      newExpanded.add(fileId);
    }
    setExpandedFiles(newExpanded);
  };

  // Filter uploads based on search term
  const filteredUploads = uploads.filter(upload => 
    upload.filename.toLowerCase().includes(searchTerm.toLowerCase()) ||
    upload.dropshipperEmail?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleDownload = async (sessionId: string, filename: string, type = 'orders') => {
    try {
      const response = await fetch(`/api/download-original/${sessionId}?type=${type}`);

      if (!response.ok) {
        throw new Error('Download failed');
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.style.display = 'none';
      a.href = url;

      // Set appropriate filename based on type
      const baseFilename = filename.replace(/\.[^/.]+$/, '');
      let downloadFilename = filename;
      if (type === 'prices') {
        downloadFilename = `${baseFilename}_product_prices.xlsx`;
      } else if (type === 'shipping') {
        downloadFilename = `${baseFilename}_shipping_rates.xlsx`;
      } else if (type === 'all') {
        downloadFilename = `${baseFilename}_complete_data.xlsx`;
      }

      a.download = downloadFilename;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Download error:', error);
    }
  };

  if (isLoading) {
    return (
      <div className="p-4">
        <div className="animate-pulse">
          <div className="h-4 bg-gray-200 rounded w-1/4 mb-4"></div>
          <div className="space-y-2">
            {[...Array(3)].map((_, i) => (
              <div key={i} className="h-16 bg-gray-100 rounded"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <Card className="m-4">
      <CardHeader className="pb-4">
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center gap-2">
            <FileText className="h-5 w-5 text-blue-600" />
            Upload History
          </CardTitle>
          <div className="flex items-center gap-2 text-sm text-muted-foreground">
            <Package className="h-4 w-4" />
            {uploads.length} files
          </div>
        </div>

        {/* Search Bar */}
        <div className="relative">
          <Search className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
          <Input
            placeholder="Search files by name or email..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="pl-10 bg-muted/30"
            data-testid="input-search-uploads"
          />
        </div>
      </CardHeader>
      <CardContent>
        <div className="space-y-3">
          {filteredUploads.length === 0 ? (
            <div className="text-center text-muted-foreground py-12">
              {searchTerm ? (
                <div>
                  <Search className="h-12 w-12 mx-auto mb-4 text-muted-foreground/50" />
                  <p className="text-lg font-medium">à¤•à¥‹à¤ˆ files à¤¨à¤¹à¥€à¤‚ à¤®à¤¿à¤²à¥€à¤‚</p>
                  <p className="text-sm">"{searchTerm}" à¤•à¥‡ à¤²à¤¿à¤ search results à¤¨à¤¹à¥€à¤‚ à¤®à¤¿à¤²à¥‡</p>
                </div>
              ) : uploads.length === 0 ? (
                <div>
                  <FileText className="h-12 w-12 mx-auto mb-4 text-muted-foreground/50" />
                  <p className="text-lg font-medium">à¤•à¥‹à¤ˆ uploaded files à¤¨à¤¹à¥€à¤‚ à¤¹à¥ˆà¤‚</p>
                  <p className="text-sm">Upload Data tab à¤¸à¥‡ files upload à¤•à¤°à¥‡à¤‚</p>
                </div>
              ) : null}
            </div>
          ) : (
            filteredUploads.map((upload) => (
              <Collapsible 
                key={upload.id}
                open={expandedFiles.has(upload.id)}
                onOpenChange={() => toggleExpanded(upload.id)}
              >
                <div className="border rounded-lg hover:bg-muted/50 transition-all duration-200 hover:shadow-sm">
                  <div className="flex items-center justify-between p-4">
                    <div className="flex items-center gap-4 flex-1">
                      <CollapsibleTrigger asChild>
                        <Button variant="ghost" size="sm" className="p-1 hover:bg-muted/70 transition-colors">
                          <ChevronDown className={`h-4 w-4 transition-transform duration-200 ${expandedFiles.has(upload.id) ? 'rotate-180' : ''}`} />
                        </Button>
                      </CollapsibleTrigger>

                      <div className="flex-shrink-0">
                        <FileText className="h-8 w-8 text-blue-500" />
                      </div>

                      <div className="flex-1 min-w-0">
                        <div className="flex items-center gap-2 mb-1 flex-wrap">
                          <span className="font-medium truncate">
                            {upload.filename}
                          </span>
                          <Badge variant="secondary" className="font-medium">
                            {(upload.orderCount || 0).toLocaleString()} orders
                          </Badge>

                          {upload.hasProductPrices && (
                            <Badge variant="outline" className="bg-green-50 text-green-700 border-green-200 font-medium">
                              <Package className="h-3 w-3 mr-1" />
                              {(upload.productPricesCount || 0).toLocaleString()} prices
                            </Badge>
                          )}

                          {upload.hasShippingRates && (
                            <Badge variant="outline" className="bg-blue-50 text-blue-700 border-blue-200 font-medium">
                              <Truck className="h-3 w-3 mr-1" />
                              {(upload.shippingRatesCount || 0).toLocaleString()} rates
                            </Badge>
                          )}
                        </div>

                        <div className="flex items-center gap-4 text-sm text-muted-foreground">
                          <div className="flex items-center gap-1">
                            <Calendar className="h-3 w-3" />
                            {format(new Date(upload.uploadedAt), 'dd MMM yyyy, hh:mm a')}
                          </div>
                          {upload.dropshipperEmail && (
                            <div className="flex items-center gap-1">
                              <User className="h-3 w-3" />
                              {upload.dropshipperEmail}
                            </div>
                          )}
                        </div>
                      </div>
                    </div>

                    <div className="flex gap-2">
                      <DropdownMenu>
                        <DropdownMenuTrigger asChild>
                          <Button
                            variant="outline"
                            size="sm"
                            data-testid={`button-download-${upload.id}`}
                            className="flex items-center gap-2 hover:bg-blue-50 hover:text-blue-600 hover:border-blue-200 transition-colors"
                          >
                            <Download className="h-4 w-4" />
                            Download
                            <ChevronDown className="h-3 w-3" />
                          </Button>
                        </DropdownMenuTrigger>
                        <DropdownMenuContent align="end" className="w-48">
                          <DropdownMenuItem onClick={() => handleDownload(upload.id, upload.filename, 'orders')}>
                            <FileText className="h-4 w-4 mr-2" />
                            Order Data Only
                          </DropdownMenuItem>

                          <DropdownMenuItem onClick={() => handleDownload(upload.id, upload.filename, 'prices')}>
                            <Package className="h-4 w-4 mr-2 text-green-600" />
                            Product Prices Only
                          </DropdownMenuItem>

                          <DropdownMenuItem onClick={() => handleDownload(upload.id, upload.filename, 'shipping')}>
                            <Truck className="h-4 w-4 mr-2 text-blue-600" />
                            Shipping Rates Only
                          </DropdownMenuItem>

                          <DropdownMenuItem onClick={() => handleDownload(upload.id, upload.filename, 'all')}>
                            <Database className="h-4 w-4 mr-2 text-purple-600" />
                            Complete Data
                          </DropdownMenuItem>
                        </DropdownMenuContent>
                      </DropdownMenu>
                    </div>
                  </div>

                  <CollapsibleContent>
                    <div className="px-4 pb-4 border-t bg-gradient-to-r from-muted/20 to-muted/10">
                      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mt-4">
                        <div className="flex items-start gap-3 p-3 rounded-lg bg-green-50/50 border border-green-100">
                          <Package className="h-5 w-5 text-green-600 mt-0.5" />
                          <div>
                            <div className="font-medium text-green-800">Product Prices</div>
                            <div className="text-sm text-green-600">
                              {upload.hasProductPrices ? 
                                `${(upload.productPricesCount || 0).toLocaleString()} items uploaded` : 
                                'Not included in this file'
                              }
                            </div>
                          </div>
                        </div>

                        <div className="flex items-start gap-3 p-3 rounded-lg bg-blue-50/50 border border-blue-100">
                          <Truck className="h-5 w-5 text-blue-600 mt-0.5" />
                          <div>
                            <div className="font-medium text-blue-800">Shipping Rates</div>
                            <div className="text-sm text-blue-600">
                              {upload.hasShippingRates ? 
                                `${(upload.shippingRatesCount || 0).toLocaleString()} rates uploaded` : 
                                'Not included in this file'
                              }
                            </div>
                          </div>
                        </div>

                        <div className="flex items-start gap-3 p-3 rounded-lg bg-purple-50/50 border border-purple-100">
                          <DollarSign className="h-5 w-5 text-purple-600 mt-0.5" />
                          <div>
                            <div className="font-medium text-purple-800">Order Data</div>
                            <div className="text-sm text-purple-600">
                              {(upload.orderCount || 0).toLocaleString()} orders processed
                            </div>
                          </div>
                        </div>
                      </div>

                      {upload.dropshipperEmail && (
                        <div className="mt-3 pt-3 border-t">
                          <span className="text-sm text-muted-foreground">
                            <span className="font-medium">Dropshipper:</span> {upload.dropshipperEmail}
                          </span>
                        </div>
                      )}
                    </div>
                  </CollapsibleContent>
                </div>
              </Collapsible>
            ))
          )}
        </div>

        {uploads.length > 0 && (
          <div className="mt-6 pt-4 border-t text-sm text-muted-foreground">
            <div className="flex items-center justify-between">
              <span>
                Total {uploads.length} files uploaded
                {searchTerm && ` â€¢ Showing ${filteredUploads.length} results`}
              </span>
              <span className="text-xs">
                {uploads.reduce((sum, upload) => sum + (upload.orderCount || 0), 0).toLocaleString()} orders total
              </span>
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/upload-progress.ts.content:
import { useEffect, useState } from "react";
import { CheckCircle, AlertCircle, Clock } from "lucide-react";
import { Card, CardContent } from "@/components/ui/card";
import { Progress } from "@/components/ui/progress";

interface UploadProgressProps {
  isUploading: boolean;
  uploadSuccess: {
    processedRows: number;
    cancelledRows: number;
  } | null;
  uploadError: string | null;
}

export default function UploadProgress({ isUploading, uploadSuccess, uploadError }: UploadProgressProps) {
  const [progress, setProgress] = useState(0);
  const [timeElapsed, setTimeElapsed] = useState(0);

  useEffect(() => {
    let interval: NodeJS.Timeout;
    if (isUploading) {
      setProgress(0);
      setTimeElapsed(0);

      // Ultra-fast progress simulation for optimized processing
      interval = setInterval(() => {
        setProgress(prev => {
          if (prev < 60) return prev + Math.random() * 8; // Very fast initial progress
          if (prev < 85) return prev + Math.random() * 4; // Fast progress  
          if (prev < 95) return prev + Math.random() * 1; // Slower near end
          return prev; // Stop at 95% until completion
        });
        setTimeElapsed(prev => prev + 1);
      }, 600); // Even faster updates
    } else if (uploadSuccess) {
      setProgress(100);
    } else {
      setProgress(0);
      setTimeElapsed(0);
    }

    return () => {
      if (interval) clearInterval(interval);
    };
  }, [isUploading, uploadSuccess]);

  if (!isUploading && !uploadSuccess && !uploadError) {
    return null;
  }

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <Card className="mb-6">
      <CardContent className="p-4">
        {isUploading && (
          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-2">
                <Clock className="h-4 w-4 text-blue-500" />
                <span className="text-sm font-medium">Processing Excel file...</span>
              </div>
              <span className="text-sm text-gray-500">{formatTime(timeElapsed)}</span>
            </div>

            <div className="space-y-2">
              <div className="flex justify-between text-sm">
                <span>Progress</span>
                <span>{Math.round(progress)}%</span>
              </div>
              <Progress value={progress} className="h-2" />
            </div>

            <div className="text-sm text-gray-600 space-y-1">
              {progress < 20 && <p className="text-blue-600">â€¢ Reading and parsing Excel data...</p>}
              {progress >= 20 && progress < 40 && <p className="text-blue-600">â€¢ Validating column mappings...</p>}
              {progress >= 40 && progress < 95 && <p className="text-blue-600">â€¢ Ultra-fast parallel processing to database...</p>}
              {progress >= 95 && <p className="text-green-600">â€¢ Finalizing upload process...</p>}
              <p className="text-amber-600 font-medium mt-2">Please keep this page open</p>
              <p className="text-sm text-gray-500">âš¡ Parallel processing with 100-record batches for maximum reliability</p>
            </div>
          </div>
        )}

        {uploadSuccess && (
          <div className="flex items-center space-x-3 text-green-700">
            <CheckCircle className="h-5 w-5" />
            <div>
              <div className="font-medium">Upload completed successfully!</div>
              <div className="text-sm">
                Processed {uploadSuccess.processedRows} orders
                {uploadSuccess.cancelledRows > 0 && `, removed ${uploadSuccess.cancelledRows} cancelled orders`}
              </div>
            </div>
          </div>
        )}

        {uploadError && (
          <div className="flex items-center space-x-3 text-red-700">
            <AlertCircle className="h-5 w-5" />
            <div>
              <div className="font-medium">Upload failed</div>
              <div className="text-sm">{uploadError}</div>
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

client/src/lib/excel-utils.ts.content:
import * as XLSX from 'xlsx';

export interface ExcelColumn {
  header: string;
  key: string;
  width?: number;
  type?: 'text' | 'number' | 'currency' | 'date';
}

export interface ExcelSheet {
  name: string;
  columns: ExcelColumn[];
  data: Record<string, any>[];
}

export function createWorkbook(sheets: ExcelSheet[]): XLSX.WorkBook {
  const workbook = XLSX.utils.book_new();

  sheets.forEach(sheet => {
    const worksheet = XLSX.utils.json_to_sheet(sheet.data, {
      header: sheet.columns.map(col => col.key)
    });

    // Set column headers
    const headerRow = sheet.columns.map(col => col.header);
    XLSX.utils.sheet_add_aoa(worksheet, [headerRow], { origin: "A1" });

    // Set column widths
    const colWidths = sheet.columns.map(col => ({
      width: col.width || 15
    }));
    worksheet['!cols'] = colWidths;

    // Add sheet to workbook
    XLSX.utils.book_append_sheet(workbook, worksheet, sheet.name);
  });

  return workbook;
}

export function downloadWorkbook(workbook: XLSX.WorkBook, filename: string) {
  const buffer = XLSX.write(workbook, { type: 'array', bookType: 'xlsx' });
  const blob = new Blob([buffer], { type: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet' });

  const url = window.URL.createObjectURL(blob);
  const a = document.createElement('a');
  a.style.display = 'none';
  a.href = url;
  a.download = filename;
  document.body.appendChild(a);
  a.click();
  window.URL.revokeObjectURL(url);
  document.body.removeChild(a);
}

export function generateTemplateSheets(): ExcelSheet[] {
  return [
    {
      name: 'Price_Request',
      columns: [
        { header: 'dropshipper_email', key: 'dropshipperEmail', width: 25 },
        { header: 'product_uid', key: 'productUid', width: 20 },
        { header: 'product_name', key: 'productName', width: 30 },
        { header: 'sku', key: 'sku', width: 15 },
        { header: 'product_cost_per_unit', key: 'productCostPerUnit', type: 'currency', width: 20 },
        { header: 'currency', key: 'currency', width: 10 },
      ],
      data: []
    },
    {
      name: 'Shipping_Rate_Request',
      columns: [
        { header: 'shipping_provider', key: 'shippingProvider', width: 25 },
        { header: 'shipping_rate_per_order', key: 'shippingRatePerOrder', type: 'currency', width: 25 },
        { header: 'currency', key: 'currency', width: 10 },
      ],
      data: []
    }
  ];
}

export function formatCurrency(amount: number, currency: string = 'INR'): string {
  return new Intl.NumberFormat('en-IN', {
    style: 'currency',
    currency,
    maximumFractionDigits: 2,
  }).format(amount);
}

export function parseCSVFile(file: File): Promise<any[]> {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const text = e.target?.result as string;
        const lines = text.split('\n');
        const headers = lines[0].split(',').map(h => h.trim());

        const data = lines.slice(1)
          .filter(line => line.trim() !== '')
          .map(line => {
            const values = line.split(',').map(v => v.trim());
            const row: Record<string, string> = {};
            headers.forEach((header, index) => {
              row[header] = values[index] || '';
            });
            return row;
          });

        resolve(data);
      } catch (error) {
        reject(error);
      }
    };
    reader.onerror = () => reject(new Error('Failed to read file'));
    reader.readAsText(file);
  });
}

