      link.setAttribute("href", encodedUri);
      link.setAttribute("download", `good_delivery_pincodes_${appliedDeliveredDateFrom}_${appliedDeliveredDateTo}.csv`);
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);

