        pincodeData.map(row => 
          `${row.pincode_area},${row.total_orders},${row.delivered_orders},${row.rts_rto_orders},${row.rto_percentage}%,â‚¹${row.delivered_cod_value},â‚¹${row.rts_rto_cod_loss}`
