            </TabsTrigger>
          </TabsList>

          {/* Settings Tab */}
          <TabsContent value="settings" className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle>Settlement Configuration â€¢ à¤¸à¥‡à¤Ÿà¤²à¤®à¥‡à¤‚à¤Ÿ à¤•à¥‰à¤¨à¥à¤«à¤¼à¤¿à¤—à¤°à¥‡à¤¶à¤¨</CardTitle>
                <CardDescription>
                  Configure frequency, dates, and D+2 mode for automated settlements
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-6">
                {/* Frequency Selection */}
                <div className="space-y-3">
                  <label className="text-sm font-medium">Frequency â€¢ à¤†à¤µà¥ƒà¤¤à¥à¤¤à¤¿</label>
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    {Object.entries(frequencyLabels).map(([key, info]) => (
                      <Card 
                        key={key}
